select te.tconst, tb.original_title, te.season_number, te.episode_number, tr.average_rating from imdb.title_episode te left join imdb.title_ratings tr on tr.tconst = te.tconst left join imdb.title_basics tb on tb.tconst = te.tconst where te.parent_tconst = 'tt0944947' order by tr.average_rating asc limit 5;